/*---
info: match returns array as specified in 15.10.6.2
es5id: 15.5.4.10_A2_T1
description: Regular expression is 3
---*/

var __string = "1234567890";

//////////////////////////////////////////////////////////////////////////////
//CHECK#1
assert.sameValue(__string.match(3)[0], "3", '#1: __string = "1234567890"; __string.match(3)[0]=== "3"');
//
//////////////////////////////////////////////////////////////////////////////

//////////////////////////////////////////////////////////////////////////////
//CHECK#2
assert.sameValue(__string.match(3).length, 1, '#2: __string = "1234567890"; __string.match(3).length ===1');
//
//////////////////////////////////////////////////////////////////////////////

//////////////////////////////////////////////////////////////////////////////
//CHECK#3
assert.sameValue(__string.match(3).index, 2, '#3: __string = "1234567890"; __string.match(3).index ===2');
//
//////////////////////////////////////////////////////////////////////////////

//////////////////////////////////////////////////////////////////////////////
//CHECK#4
assert.sameValue(__string.match(3).input, __string, '#4: __string = "1234567890"; __string.match(3).input ===__string');
//
//////////////////////////////////////////////////////////////////////////////
