/*---
info: match returns array as specified in 15.10.6.2
es5id: 15.5.4.10_A2_T17
description: Regular expression is /0./
---*/

var __re = /0./;

var __num = 10203040506070809000;

Number.prototype.match = String.prototype.match;

//__matches = ["02"];

//////////////////////////////////////////////////////////////////////////////
//CHECK#1
assert.sameValue(__num.match(__re)[0], "02", '#1: __num.match(__re)[0]=== "02"');
//
//////////////////////////////////////////////////////////////////////////////

//////////////////////////////////////////////////////////////////////////////
//CHECK#2
assert.sameValue(__num.match(__re).length, 1, '#2: __num.match(__re).length ===1');
//
//////////////////////////////////////////////////////////////////////////////

//////////////////////////////////////////////////////////////////////////////
//CHECK#3
assert.sameValue(__num.match(__re).index, 1, '#3: __num.match(__re).index ===1');
//
//////////////////////////////////////////////////////////////////////////////

//////////////////////////////////////////////////////////////////////////////
//CHECK#4
assert.sameValue(__num.match(__re).input, String(__num), '#4: __num.match(__re).input ===String(__num)');
//
//////////////////////////////////////////////////////////////////////////////
